package Lec4;
import java.util.*;
public class Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Operator 
		Scanner sc = new Scanner(System.in);
		int x = sc.nextInt();
		int y = sc.nextInt();
		System.out.println(x);
		System.out.println(y);
		//  Arthimatic + , - , * ,/ ,% ,++,--
		System.out.println("x+y "+ (x+y) );
		System.out.println("x-y "+ (x-y) );
		System.out.println("x*y "+ x*y);
		System.out.println("x%y "+ x%y);
		System.out.println("x/y "+ x/y);

		// 
	}

}
