package Lec7;

import java.util.Scanner;


public class pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		int star = n;
		int i = 1;
		while (i<=star) {
			System.out.print("*"+" ");
			i++;
		}
	}

}
